//Global Vars
int v[NMAX], n;

//Structs

//Solve Functions
inline void debug_vector(int v[NMAX]){
    fprintf(stderr, "== VECTOR ==\n");
    for(int i=0; i<n; i++)
        fprintf(stderr, "%d ", v[i]);
    fprintf(stderr, "\n");
}

unsigned long long sum(int line, int to){
    unsigned long long s = 0;
    for(int i=0; i<to; i++)
        s += tpascal[line][i];
    return s;
}

/*void minus1(int from, int etalon){
    for(int i=from; i<n; i++)
        if(v[i] > etalon)
            v[i]--;
        else
            v[i] = etalon - v[i];
}
*/
int inversiuni(int v[NMAX], int start){
    int s=0;
    int i,j;
    for(i=start; i<n; i++){
        for(j=i+1; j<n; j++)
            if (v[i] > v[j])
                s++;
    }
    return s;
}

int main(){
    freopen("nrperm.in","r",stdin);
    //freopen("nrperm.out","w",stdout);

    unsigned long long sol = 0;
    int i, j;

    scanf("%d", &n);
    for (i=0; i<n; i++)
        scanf("%d", &v[i]);

    sol += sum(n-1, v[0] - 1);
    sol += inversiuni(v,0);

    //debug_vector(v);

    int myv[NMAX];
    for(i=0, j=v[0]; j>=1; i++, j--)
        myv[i] = j;
    for(j=v[0]+1;i<n;j++,i++)
        myv[i] = j;

    if (n > 4){
        for(i=1; i<n-3; i++)
            if(v[i] > v[i-1])
                ++sol;
    }

    sol -= inversiuni(myv,0);
    //debug_vector(myv);

    printf("%llu\n", sol+1);

    return 0;
}
